import React, { useEffect } from 'react'
import FormAddLinks from './FormAddLinks'
import {useSelector,useDispatch} from 'react-redux'
import axios from 'axios'
const api = 'https://randomuser.me/api/'

export default function Profil({userName}) {

    const dispatch = useDispatch()
    const {name, imageProfil,ready} = useSelector(state=> ({...state.profilReducer}))

    const  getinformation =  () =>
    {
             axios.get(api).then(res => {
                const firstname = res.data.results[0].name.first
                console.log("firstname",firstname)
                dispatch({
                    type: 'LOAD',
                    name: firstname,
                    ready:true,
                })
            })
    }
    useEffect(() => {
        getinformation()
    },[])
    return (
        <div>
            <img src={'../../../public/logo192.png'}/>
            <header>
                <h1>Profil de : {userName}</h1>
            </header>
            {ready &&(<p>{name}</p>)}
            <button onClick={getinformation}>Load</button>
            {/* <img src = {imageProfil} /> */}
            <div>
                <button> Ajoutez un Lien </button>
                <FormAddLinks />
            </div>
        </div>
    )
}
