import axios from 'axios'
const api = 'https://randomuser.me/api/'
const getinformation=()=> {
    return axios.get(api).then(res => {
        return res.data[0]
    })
}
//const api = "https://character-database.becode.xyz/characters";
const USER_INFORMATIONS= {
   name: 'salam',
    imageProfil:'https://randomuser.me/api/portraits/women/79.jpg',
    links:{
    }

}


function profilReducer (state = USER_INFORMATIONS, action ){

    if(action.type){
        return state
    }

    return state
}
export default profilReducer
